content:
/**
 * Estilos espec√≠ficos para o componente RecordingDetail
 * Visualiza√ß√£o detalhada com tema dark e alta qualidade visual
 */

/* Container principal dos detalhes */
.recording-detail {
  display: flex;
  flex-direction: column;
  height: 100%;
  background-color: var(--bg-dark);
  color: var(--text-primary);
}

/* Header dos detalhes */
.recording-detail-header {
  padding: var(--spacing-lg);
  background-color: var(--bg-darker);
  border-bottom: 1px solid var(--border-color);
  display: flex;
  align-items: flex-start;
  justify-content: space-between;
  gap: var(--spacing-md);
}

.recording-detail-header-info {
  flex: 1;
  min-width: 0;
}

.recording-detail-title {
  font-size: var(--font-lg);
  font-weight: 600;
  display: flex;
  align-items: center;
  gap: var(--spacing-sm);
  margin: 0 0 var(--spacing-sm) 0;
  color: var(--text-primary);
}

.recording-detail-title-text {
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

/* Metadados */
.recording-detail-meta {
  display: flex;
  flex-wrap: wrap;
  gap: var(--spacing-lg);
  font-size: var(--font-sm);
  color: var(--text-secondary);
}

.recording-detail-meta-item {
  display: flex;
  align-items: center;
  gap: var(--spacing-xs);
}

.recording-detail-meta-icon {
  color: var(--text-muted);
}

.recording-detail-meta-url {
  max-width: 200px;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

/* √Årea de conte√∫do */
.recording-detail-content {
  flex: 1;
  padding: var(--spacing-lg);
  overflow: hidden;
  display: flex;
  flex-direction: column;
  gap: var(--spacing-lg);
}

/* Barra de ferramentas */
.recording-detail-toolbar {
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: var(--spacing-md);
  flex-wrap: wrap;
}

/* Tabs de visualiza√ß√£o */
.recording-detail-tabs {
  display: flex;
  gap: var(--spacing-sm);
  background-color: var(--bg-card);
  padding: var(--spacing-xs);
  border-radius: var(--radius-md);
}

.recording-detail-tab {
  padding: var(--spacing-sm) var(--spacing-md);
  background-color: transparent;
  border: none;
  border-radius: var(--radius-sm);
  color: var(--text-secondary);
  font-size: var(--font-sm);
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s ease;
  display: flex;
  align-items: center;
  gap: var(--spacing-xs);
}

.recording-detail-tab:hover {
  color: var(--text-primary);
  background-color: var(--bg-hover);
}

.recording-detail-tab.active {
  background-color: var(--bg-darker);
  color: var(--primary);
}

/* A√ß√µes da toolbar */
.recording-detail-actions {
  display: flex;
  align-items: center;
  gap: var(--spacing-md);
}

.recording-detail-script-type {
  font-size: var(--font-sm);
  color: var(--text-muted);
  padding: var(--spacing-xs) var(--spacing-sm);
  background-color: var(--bg-card);
  border-radius: var(--radius-sm);
  font-weight: 500;
}

/* √Årea de visualiza√ß√£o */
.recording-detail-view {
  flex: 1;
  overflow: hidden;
  background-color: var(--bg-card);
  border-radius: var(--radius-lg);
  border: 1px solid var(--border-color);
}

/* Container de a√ß√µes */
.recording-detail-actions-view {
  height: 100%;
  overflow-y: auto;
  padding: var(--spacing-lg);
}

/* Container de c√≥digo */
.recording-detail-code-view {
  height: 100%;
  display: flex;
  flex-direction: column;
}

.recording-detail-code-header {
  padding: var(--spacing-md) var(--spacing-lg);
  background-color: var(--bg-darker);
  border-bottom: 1px solid var(--border-color);
  display: flex;
  align-items: center;
  justify-content: space-between;
}

.recording-detail-code-title {
  font-size: var(--font-sm);
  font-weight: 600;
  color: var(--text-secondary);
}

.recording-detail-code-content {
  flex: 1;
  overflow: auto;
  padding: var(--spacing-lg);
  background-color: var(--bg-code);
  font-family: var(--font-mono);
  font-size: var(--font-sm);
  line-height: 1.6;
}

/* Bot√µes personalizados */
.recording-btn {
  padding: var(--spacing-sm) var(--spacing-md);
  border: none;
  border-radius: var(--radius-md);
  font-size: var(--font-sm);
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s ease;
  display: inline-flex;
  align-items: center;
  gap: var(--spacing-xs);
  white-space: nowrap;
}

.recording-btn:hover {
  transform: translateY(-1px);
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.2);
}

.recording-btn:active {
  transform: translateY(0);
  box-shadow: none;
}

.recording-btn-primary {
  background-color: var(--primary);
  color: var(--white);
}

.recording-btn-primary:hover {
  background-color: var(--primary-hover);
}

.recording-btn-secondary {
  background-color: var(--bg-button);
  color: var(--text-primary);
  border: 1px solid var(--border-color);
}

.recording-btn-secondary:hover {
  background-color: var(--bg-hover);
  border-color: var(--border-hover);
}

.recording-btn-success {
  background-color: var(--success);
  color: var(--white);
}

.recording-btn-back {
  background-color: var(--bg-button);
  color: var(--text-primary);
  border: 1px solid var(--border-color);
}

.recording-btn-back:hover {
  background-color: var(--bg-hover);
  border-color: var(--primary);
  color: var(--primary);
}

/* Bot√£o danger para parar replay */
.recording-btn-danger {
  background-color: #dc3545;
  color: white;
}

.recording-btn-danger:hover {
  background-color: #c82333;
}

/* Select para modo de replay */
.recording-select {
  padding: var(--spacing-sm) var(--spacing-md);
  border: 1px solid var(--border-color);
  border-radius: var(--radius-md);
  background-color: var(--bg-button);
  color: var(--text-primary);
  font-size: var(--font-sm);
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s ease;
  min-width: 130px;
}

.recording-select:hover {
  background-color: var(--bg-hover);
  border-color: var(--border-hover);
}

.recording-select:focus {
  outline: none;
  border-color: var(--primary);
  box-shadow: 0 0 0 2px rgba(var(--primary-rgb), 0.2);
}

/* Status do replay */
.recording-detail-replay-status {
  background-color: var(--bg-darker);
  border-bottom: 1px solid var(--border-color);
  padding: var(--spacing-md) var(--spacing-lg);
  display: flex;
  align-items: center;
  gap: var(--spacing-sm);
}

.recording-detail-replay-status.error {
  background-color: rgba(220, 53, 69, 0.1);
  border-color: rgba(220, 53, 69, 0.3);
}

.recording-detail-replay-status-content {
  display: flex;
  align-items: center;
  gap: var(--spacing-sm);
  flex: 1;
  color: var(--text-secondary);
  font-size: var(--font-sm);
}

.recording-detail-replay-status.error .recording-detail-replay-status-content {
  color: #dc3545;
}

/* Barra de progresso do replay */
.recording-detail-replay-progress {
  flex: 1;
  height: 4px;
  background-color: var(--bg-lightest);
  border-radius: 2px;
  overflow: hidden;
  margin-left: var(--spacing-md);
}

.recording-detail-replay-progress-bar {
  height: 100%;
  background-color: var(--accent-color);
  transition: width 0.3s ease-out;
}

/* Estados especiais */
.recording-detail-loading {
  display: flex;
  align-items: center;
  justify-content: center;
  height: 100%;
  color: var(--text-secondary);
  gap: var(--spacing-sm);
}

.recording-detail-error {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100%;
  padding: var(--spacing-xl);
  text-align: center;
}

.recording-detail-error-icon {
  font-size: 48px;
  color: var(--error);
  margin-bottom: var(--spacing-lg);
}

.recording-detail-error-text {
  color: var(--text-secondary);
  margin-bottom: var(--spacing-md);
}

/* Responsividade */
@media (max-width: 600px) {
  .recording-detail-header {
    flex-direction: column;
    align-items: stretch;
    padding: var(--spacing-md);
  }

  .recording-detail-content {
    padding: var(--spacing-md);
  }

  .recording-detail-toolbar {
    flex-direction: column;
    align-items: stretch;
  }

  .recording-detail-tabs {
    width: 100%;
    justify-content: center;
  }

  .recording-detail-actions {
    width: 100%;
    justify-content: center;
  }

  .recording-detail-meta {
    font-size: var(--font-xs);
    gap: var(--spacing-md);
  }
}

/* Anima√ß√µes */
@keyframes fadeIn {
  from {
    opacity: 0;
    transform: translateY(10px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.recording-detail-view {
  animation: fadeIn 0.3s ease-out;
}

/* Scrollbar customizada */
.recording-detail-actions-view::-webkit-scrollbar,
.recording-detail-code-content::-webkit-scrollbar {
  width: 8px;
}

.recording-detail-actions-view::-webkit-scrollbar-track,
.recording-detail-code-content::-webkit-scrollbar-track {
  background: var(--bg-darker);
  border-radius: var(--radius-sm);
}

.recording-detail-actions-view::-webkit-scrollbar-thumb,
.recording-detail-code-content::-webkit-scrollbar-thumb {
  background: var(--scrollbar-thumb);
  border-radius: var(--radius-sm);
}

.recording-detail-actions-view::-webkit-scrollbar-thumb:hover,
.recording-detail-code-content::-webkit-scrollbar-thumb:hover {
  background: var(--scrollbar-thumb-hover);
}

/**
 * ReplayRunner - M√≥dulo respons√°vel por executar as a√ß√µes gravadas em nova aba
 * Injeta script na p√°gina para reproduzir intera√ß√µes do usu√°rio
 */

import type { Action } from '../../pages/types/index';
import { ActionType } from '../../pages/types/index';
import type { ActionExecutorOptions, ActionExecutionResult, ReplayExecute, ReplayResult } from '../../types/replay';
import { ReplayMode } from '../../types/replay';

/**
 * Executa uma a√ß√£o de clique no elemento
 */
async function executeClick(selector: string, options: ActionExecutorOptions): Promise<ActionExecutionResult> {
  const startTime = Date.now();
  const { retryAttempts = 3, retryDelay = 500, waitTimeout = 5000 } = options;

  for (let attempt = 0; attempt < retryAttempts; attempt++) {
    try {
      // Aguarda elemento aparecer com timeout
      const element = await waitForElement(selector, waitTimeout);

      if (!element) {
        throw new Error(`Elemento n√£o encontrado: ${selector}`);
      }

      // Scroll at√© elemento se necess√°rio
      element.scrollIntoView({ behavior: 'smooth', block: 'center' });
      await sleep(100);

      // Simula clique real
      const clickEvent = new MouseEvent('click', {
        bubbles: true,
        cancelable: true,
        view: window
      });

      element.dispatchEvent(clickEvent);

      return {
        success: true,
        duration: Date.now() - startTime
      };
    } catch (error) {
      if (attempt === retryAttempts - 1) {
        return {
          success: false,
          error: error instanceof Error ? error.message : 'Erro desconhecido',
          duration: Date.now() - startTime
        };
      }
      await sleep(retryDelay);
    }
  }

  return {
    success: false,
    error: 'M√°ximo de tentativas excedido',
    duration: Date.now() - startTime
  };
}

/**
 * Executa uma a√ß√£o de digita√ß√£o em input
 */
async function executeType(selector: string, value: string, options: ActionExecutorOptions): Promise<ActionExecutionResult> {
  const startTime = Date.now();
  const { retryAttempts = 3, retryDelay = 500, waitTimeout = 5000 } = options;

  for (let attempt = 0; attempt < retryAttempts; attempt++) {
    try {
      const element = await waitForElement(selector, waitTimeout) as HTMLInputElement | HTMLTextAreaElement;

      if (!element) {
        throw new Error(`Elemento n√£o encontrado: ${selector}`);
      }

      // Foca no elemento
      element.focus();
      await sleep(50);

      // Limpa valor existente
      element.value = '';

      // Digita caractere por caractere para simular usu√°rio real
      for (const char of value) {
        element.value += char;
        element.dispatchEvent(new Event('input', { bubbles: true }));
        await sleep(20 + Math.random() * 30); // Varia√ß√£o para parecer humano
      }

      // Dispara evento change
      element.dispatchEvent(new Event('change', { bubbles: true }));

      return {
        success: true,
        duration: Date.now() - startTime
      };
    } catch (error) {
      if (attempt === retryAttempts - 1) {
        return {
          success: false,
          error: error instanceof Error ? error.message : 'Erro desconhecido',
          duration: Date.now() - startTime
        };
      }
      await sleep(retryDelay);
    }
  }

  return {
    success: false,
    error: 'M√°ximo de tentativas excedido',
    duration: Date.now() - startTime
  };
}

/**
 * Executa navega√ß√£o para nova URL
 */
async function executeNavigate(url: string): Promise<ActionExecutionResult> {
  const startTime = Date.now();

  try {
    window.location.href = url;

    return {
      success: true,
      duration: Date.now() - startTime
    };
  } catch (error) {
    return {
      success: false,
      error: error instanceof Error ? error.message : 'Erro ao navegar',
      duration: Date.now() - startTime
    };
  }
}

/**
 * Aguarda elemento aparecer no DOM
 */
async function waitForElement(selector: string, timeout: number): Promise<Element | null> {
  const startTime = Date.now();

  return new Promise((resolve) => {
    // Tenta encontrar imediatamente
    const element = document.querySelector(selector);
    if (element) {
      resolve(element);
      return;
    }

    // Observer para detectar quando elemento aparecer
    const observer = new MutationObserver(() => {
      const element = document.querySelector(selector);
      if (element) {
        observer.disconnect();
        resolve(element);
      } else if (Date.now() - startTime > timeout) {
        observer.disconnect();
        resolve(null);
      }
    });

    observer.observe(document.body, {
      childList: true,
      subtree: true
    });

    // Timeout fallback
    setTimeout(() => {
      observer.disconnect();
      resolve(null);
    }, timeout);
  });
}

/**
 * Aguarda tempo especificado
 */
function sleep(ms: number): Promise<void> {
  return new Promise(resolve => setTimeout(resolve, ms));
}

/**
 * Limpa cache do navegador e recarrega a p√°gina
 */
async function clearCacheAndReload(): Promise<void> {
  try {
    // Limpa localStorage
    if (window.localStorage) {
      window.localStorage.clear();
    }

    // Limpa sessionStorage
    if (window.sessionStorage) {
      window.sessionStorage.clear();
    }

    // Limpa cookies do dom√≠nio atual
    if (document.cookie) {
      document.cookie.split(";").forEach((c) => {
        document.cookie = c.replace(/^ +/, "").replace(/=.*/, "=;expires=" + new Date().toUTCString() + ";path=/");
      });
    }

    // For√ßa recarga sem cache
    window.location.reload();

    // Aguarda a p√°gina recarregar
    await new Promise(resolve => {
      window.addEventListener('load', () => resolve(void 0));
    });
  } catch (error) {
    console.error('Erro ao limpar cache:', error);
  }
}

/**
 * Fun√ß√£o principal do runner - executada no contexto da p√°gina
 */
async function runReplay(message: ReplayExecute): Promise<void> {
  const { actions, initialUrl, mode } = message;
  let completedSteps = 0;

  try {
    // Se modo clean-cache, limpa cache e recarrega primeiro
    if (mode === ReplayMode.CLEAN_CACHE && window.location.href === initialUrl) {
      await clearCacheAndReload();
      // Ap√≥s reload, n√£o precisa continuar pois a p√°gina foi recarregada
      // O script ser√° reinjetado e continuar√° a partir daqui
      return;
    }

    // Navega para URL inicial se diferente
    if (window.location.href !== initialUrl) {
      await executeNavigate(initialUrl);
      // Aguarda p√°gina carregar completamente
      await new Promise(resolve => {
        if (document.readyState === 'complete') {
          resolve(void 0);
        } else {
          window.addEventListener('load', () => resolve(void 0));
        }
      });

      // Se modo clean-cache e acabou de navegar, limpa cache e recarrega
      if (mode === ReplayMode.CLEAN_CACHE) {
        await clearCacheAndReload();
        return;
      }
    }

    // Executa cada a√ß√£o respeitando delays
    for (let i = 0; i < actions.length; i++) {
      const action = actions[i];
      const nextAction = actions[i + 1];

      // Calcula delay at√© pr√≥xima a√ß√£o
      const delay = nextAction 
        ? nextAction.timestamp - action.timestamp 
        : 500; // Delay padr√£o para √∫ltima a√ß√£o

      // Executa a√ß√£o baseada no tipo
      let result: ActionExecutionResult;

      // Get the first available selector
      const selector = action.selectors ? Object.values(action.selectors).find(s => s !== null) || '' : '';

      switch (action.type) {
        case ActionType.Click:
          result = await executeClick(selector, {});
          break;

        case ActionType.Input:
          result = await executeType(selector, action.value || '', {});
          break;

        case ActionType.Navigate:
          const navAction = action as any; // Type assertion for NavigateAction
          result = await executeNavigate(navAction.url || '');
          break;

        default:
          result = { success: false, error: `Tipo de a√ß√£o n√£o suportado: ${action.type}` };
      }

      if (!result.success) {
        throw new Error(`Falha na a√ß√£o ${i + 1}: ${result.error}`);
      }

      completedSteps++;

      // Aguarda delay natural entre a√ß√µes
      await sleep(Math.min(delay, 3000)); // M√°ximo 3 segundos entre a√ß√µes
    }

    // Envia resultado de sucesso
    chrome.runtime.sendMessage<ReplayResult>({
      type: 'REPLAY_RESULT',
      success: true,
      completedSteps
    });

  } catch (error) {
    // Envia resultado de erro
    chrome.runtime.sendMessage<ReplayResult>({
      type: 'REPLAY_RESULT',
      success: false,
      error: error instanceof Error ? error.message : 'Erro desconhecido',
      completedSteps
    });
  }
}

// Listener para mensagens do background
chrome.runtime.onMessage.addListener((message: any) => {
  if (message.type === 'REPLAY_EXECUTE') {
    runReplay(message as ReplayExecute);
  }
});

// Exporta para testes
export { executeClick, executeType, executeNavigate, waitForElement };

/**
 * ReplayHandler - Gerencia comunica√ß√£o entre popup e runner para execu√ß√£o de replay
 * Coordena abertura de nova aba, inje√ß√£o de script e monitoramento de status
 */

import type { ReplayRequest, ReplayState, ReplayStatusUpdate, ReplayExecute, ReplayResult } from '../../types/replay';
import type { RecordingEntry } from '../../pages/types/recording';
import { recordingStore } from '../../pages/storage/recording-store';

class ReplayHandler {
  private static instance: ReplayHandler;
  private replayTabs: Map<number, ReplayState>;

  private constructor() {
    this.replayTabs = new Map();
    this.setupMessageListeners();
  }

  /**
   * Obt√©m inst√¢ncia singleton do ReplayHandler
   */
  static getInstance(): ReplayHandler {
    if (!ReplayHandler.instance) {
      ReplayHandler.instance = new ReplayHandler();
    }
    return ReplayHandler.instance;
  }

  /**
   * Configura listeners para mensagens
   */
  private setupMessageListeners(): void {
    chrome.runtime.onMessage.addListener((message: any, sender, sendResponse) => {
      if (message.type === 'REPLAY_REQUEST') {
        this.handleReplayRequest(message as ReplayRequest, sendResponse);
        return true; // Indica resposta ass√≠ncrona
      }

      if (message.type === 'REPLAY_RESULT' && sender.tab?.id) {
        this.handleReplayResult(message as ReplayResult, sender.tab.id);
      }
    });

    // Limpa estado quando aba √© fechada
    chrome.tabs.onRemoved.addListener((tabId) => {
      if (this.replayTabs.has(tabId)) {
        this.replayTabs.delete(tabId);
        this.broadcastStatusUpdate(tabId, {
          status: 'idle',
          currentStepIndex: 0,
          totalSteps: 0,
          startTime: null
        });
      }
    });
  }

  /**
   * Processa requisi√ß√£o de replay
   */
  private async handleReplayRequest(
    request: ReplayRequest, 
    sendResponse: (response: any) => void
  ): Promise<void> {
    try {
      // Busca grava√ß√£o no storage
      const recording = await recordingStore.get(request.recordingId);

      if (!recording) {
        sendResponse({ success: false, error: 'Grava√ß√£o n√£o encontrada' });
        return;
      }

      // Cria nova aba
      const tab = await chrome.tabs.create({
        url: recording.url,
        active: true
      });

      if (!tab.id) {
        sendResponse({ success: false, error: 'Falha ao criar aba' });
        return;
      }

      // Inicializa estado do replay
      const state: ReplayState = {
        status: 'preparing',
        currentStepIndex: 0,
        totalSteps: recording.actions.length,
        startTime: Date.now()
      };

      this.replayTabs.set(tab.id, state);

      // Aguarda aba carregar
      const listener = (tabId: number, changeInfo: chrome.tabs.TabChangeInfo) => {
        if (tabId === tab.id && changeInfo.status === 'complete') {
          chrome.tabs.onUpdated.removeListener(listener);

          // Injeta script do runner
          chrome.scripting.executeScript({
            target: { tabId: tab.id! },
            files: ['replayRunner.bundle.js']
          }).then(() => {
            // Envia a√ß√µes para execu√ß√£o
            const executeMessage: ReplayExecute = {
              type: 'REPLAY_EXECUTE',
              actions: recording.actions,
              initialUrl: recording.url,
              mode: request.mode
            };

            chrome.tabs.sendMessage(tab.id!, executeMessage);

            // Atualiza status para running
            const runningState: ReplayState = {
              ...state,
              status: 'running'
            };

            if (tab.id) {
              this.replayTabs.set(tab.id, runningState);
              this.broadcastStatusUpdate(tab.id, runningState);
            }
          }).catch((error) => {
            sendResponse({ success: false, error: `Falha ao injetar script: ${error.message}` });
          });
        }
      };

      chrome.tabs.onUpdated.addListener(listener);

      sendResponse({ success: true, tabId: tab.id });

    } catch (error) {
      sendResponse({ 
        success: false, 
        error: error instanceof Error ? error.message : 'Erro desconhecido' 
      });
    }
  }

  /**
   * Processa resultado do replay
   */
  private handleReplayResult(result: ReplayResult, tabId: number): void {
    const state = this.replayTabs.get(tabId);

    if (!state) return;

    const updatedState: ReplayState = {
      ...state,
      status: result.success ? 'completed' : 'error',
      currentStepIndex: result.completedSteps || 0,
      error: result.error
    };

    this.replayTabs.set(tabId, updatedState);
    this.broadcastStatusUpdate(tabId, updatedState);

    // Remove estado ap√≥s 5 segundos se completado
    if (result.success) {
      setTimeout(() => {
        this.replayTabs.delete(tabId);
      }, 5000);
    }
  }

  /**
   * Envia atualiza√ß√£o de status para todos os listeners
   */
  private broadcastStatusUpdate(tabId: number, state: ReplayState): void {
    const update: ReplayStatusUpdate = {
      type: 'REPLAY_STATUS',
      state,
      tabId
    };

    // Envia para popup
    try {
      chrome.runtime.sendMessage(update);
    } catch {
      // Ignora erro se popup n√£o estiver aberto
    }
  }

  /**
   * Obt√©m estado atual do replay para uma aba
   */
  getReplayState(tabId: number): ReplayState | null {
    return this.replayTabs.get(tabId) || null;
  }

  /**
   * Para replay em execu√ß√£o
   */
  stopReplay(tabId: number): void {
    if (this.replayTabs.has(tabId)) {
      chrome.tabs.remove(tabId).catch(() => {
        // Ignora erro se aba j√° foi fechada
      });
    }
  }
}

