export const RecordingDetail: React.FC<RecordingDetailProps> = ({
  recording,
  onBack,
}) => {
  const [viewMode, setViewMode] = useState<'actions' | 'code'>('code');
  const [copied, setCopied] = useState(false);
  const [replayMode, setReplayMode] = useState<ReplayMode>(ReplayMode.KEEP_CACHE);
  const { replayState, isReplaying, startReplay, stopReplay, error } = useReplay();

  const getCypressCode = useMemo((): string => {
    console.log('üîÑ [RecordingDetail] Gerando c√≥digo para Cypress');

    // Se j√° tem o template novo, usa ele
    if (recording.code.cypressTemplate) {
      return recording.code.cypressTemplate;
    }

    // Sen√£o, gera o template novo a partir das a√ß√µes
    try {
      return genCypressCodeWithTemplate(recording.actions, {
        testName: recording.title,
        url: recording.url,
        exportOptions: {
          viewportWidth: 1366,
          viewportHeight: 768
        }
      }, true);
    } catch (error) {
      console.error('‚ùå Erro ao gerar template novo, usando c√≥digo antigo:', error);
      return recording.code.cypress;
    }
  }, [recording]);

  const handleCopy = useCallback(() => {
    console.log(
      'üìã [RecordingDetail] C√≥digo copiado para √°rea de transfer√™ncia'
    );
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
  }, []);

  const handleDownload = useCallback(() => {
    try {
      const code = getCypressCode;
      const { filename, content } = prepareTestDownload(code, recording.title);
      downloadTestFile(filename, content);
    } catch (error) {
      console.error('‚ùå [RecordingDetail] Erro ao baixar teste:', error);
    }
  }, [getCypressCode, recording.title]);

  const handleReplay = useCallback(async () => {
    try {
      console.log('üé¨ [RecordingDetail] Iniciando replay da grava√ß√£o:', recording.id, 'Modo:', replayMode);
