          {viewMode === 'actions' && (
            <div className="recording-detail-actions">
              {isReplaying ? (
                <button 
                  className="recording-btn recording-btn-danger"
                  onClick={handleStopReplay}
                >
                  <FontAwesomeIcon icon={faStop} />
                  Parar Replay
                </button>
              ) : (
                <>
                  <select 
                    className="recording-select"
                    value={replayMode}
                    onChange={(e) => setReplayMode(e.target.value as ReplayMode)}
                  >
                    <option value={ReplayMode.KEEP_CACHE}>Manter Cache</option>
                    <option value={ReplayMode.CLEAN_CACHE}>Limpar Cache</option>
                  </select>
                  <button 
                    className="recording-btn recording-btn-primary"
                    onClick={handleReplay}
                    disabled={recording.actions.length === 0}
                  >
                    <FontAwesomeIcon icon={faRedo} />
                    Reproduzir
                  </button>
                </>
              )}
            </div>
          )}
        </div>

        {/* Replay Status */}
        {replayState && replayState.status !== 'idle' && (
          <div className={`recording-detail-replay-status ${
            replayState.status === 'error' ? 'error' : ''
          }`}>
            <div className="recording-detail-replay-status-content">
              {replayState.status === 'preparing' && (
                <>
                  <FontAwesomeIcon icon={faSpinner} spin />
                  <span>Preparando replay...</span>
                </>
              )}
              {replayState.status === 'running' && (
                <>
                  <FontAwesomeIcon icon={faPlay} />
                  <span>
                    Executando: {replayState.currentStepIndex + 1} de {replayState.totalSteps} a√ß√µes
                  </span>
                  <div className="recording-detail-replay-progress">
                    <div 
                      className="recording-detail-replay-progress-bar"
                      style={{ 
                        width: `${(replayState.currentStepIndex / replayState.totalSteps) * 100}%` 
                      }}
                    />
                  </div>
                </>
              )}
              {replayState.status === 'completed' && (
                <>
                  <FontAwesomeIcon icon={faCheck} />
                  <span>Replay conclu√≠do com sucesso!</span>
                </>
              )}
              {replayState.status === 'error' && (
                <>
                  <FontAwesomeIcon icon={faSpinner} />
                  <span>Erro: {replayState.error || error || 'Erro desconhecido'}</span>
                </>
              )}
            </div>
          </div>
        )}

        {/* View Content */}
        <div className="recording-detail-view">
          {viewMode === 'actions' ? (
            <div className="recording-detail-actions-view">
              <ActionList actions={recording.actions} />
            </div>
          ) : (
            <div className="recording-detail-code-view">
              <div className="recording-detail-code-header">
                <span className="recording-detail-code-title">
                  C√≥digo Cypress Gerado
                </span>
              </div>
              <div className="recording-detail-code-content">
                <SyntaxHighlighter
                  language="javascript"
                  style={vscDarkPlus}
                  customStyle={{
                    background: 'none',
                    padding: 0,
                    overflow: 'auto',
                    paddingRight: '1em',
                    paddingBottom: '1em',
                  }}
                  data-testid="code-block"
                >
                  {getCypressCode}
                </SyntaxHighlighter>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

var webpack = require('webpack'),
  path = require('path'),
  fileSystem = require('fs-extra'),
  env = require('./utils/env'),
  { CleanWebpackPlugin } = require('clean-webpack-plugin'),
  CopyWebpackPlugin = require('copy-webpack-plugin'),
  HtmlWebpackPlugin = require('html-webpack-plugin'),
  TerserPlugin = require('terser-webpack-plugin');

const ASSET_PATH = process.env.ASSET_PATH || '/';
const MANIFEST_VERSION = process.env.MANIFEST_VERSION;

var alias = {
  'react-dom': '@hot-loader/react-dom',
};

// load the secrets
var secretsPath = path.join(__dirname, 'secrets.' + env.NODE_ENV + '.js');

var fileExtensions = [
  'jpg',
  'jpeg',
  'png',
  'gif',
  'eot',
  'otf',
  'svg',
  'ttf',
  'woff',
  'woff2',
];

if (fileSystem.existsSync(secretsPath)) {
  alias['secrets'] = secretsPath;
}

var options = {
  mode: process.env.NODE_ENV || 'development',
  entry: {
    popup: path.join(__dirname, 'src', 'pages', 'Popup', 'index.jsx'),
    background: path.join(__dirname, 'src', 'pages', 'Background', 'index.ts'),
    contentScript: path.join(__dirname, 'src', 'pages', 'Content', 'index.tsx'),
    bridge: path.join(__dirname, 'src', 'pages', 'Bridge', 'index.ts'),
    cypressTrigger: path.join(
      __dirname,
      'src',
      'pages',
      'CypressTrigger',
      'index.tsx'
    ),
    replayRunner: path.join(__dirname, 'src', 'modules', 'replay', 'replay-runner.ts'),
  },
  chromeExtensionBoilerplate: {
    notHotReload: ['contentScript' /*, 'devtools'*/],
  },
  output: {
    path: path.resolve(__dirname, 'build'),
    filename: '[name].bundle.js',
    publicPath: ASSET_PATH,
  },
  module: {
    rules: [
      {
        // Content script styles need to be embedded inline due to shadow dom usage
        test: /\.(css|scss)$/,
        type: 'asset/source',
      },
      {
        test: new RegExp('.(' + fileExtensions.join('|') + ')$'),
        loader: 'file-loader',
        options: {
          name: '[name].[ext]',
        },
        exclude: /node_modules/,
      },
      {
        test: /\.html$/,
        loader: 'html-loader',
        exclude: /node_modules/,
      },
      { test: /\.(ts|tsx)$/, loader: 'ts-loader', exclude: /node_modules/ },
      {
        test: /\.(js|jsx)$/,
