  },
};

if (env.NODE_ENV === 'development') {
  options.devtool = 'cheap-module-source-map';
} else {
  options.optimization = {
    minimize: true,
    minimizer: [
      new TerserPlugin({
        extractComments: false,
        terserOptions: {
          mangle: false,
        },
      }),
    ],
  };
}

module.exports = options;

/**
 * Tipos TypeScript para o sistema de replay de grava√ß√µes
 */

import type { Action } from '../pages/types/index';

/**
 * Modos de replay dispon√≠veis
 */
export enum ReplayMode {
  /** Mant√©m o cache atual do navegador */
  KEEP_CACHE = 'keep-cache',
  /** Limpa todo o cache antes de iniciar */
  CLEAN_CACHE = 'clean-cache'
}

/**
 * Status poss√≠veis durante o replay
 */
export type ReplayStatus = 'idle' | 'preparing' | 'running' | 'paused' | 'completed' | 'error';

/**
 * Estado do replay
 */
export interface ReplayState {
  status: ReplayStatus;
  currentStepIndex: number;
  totalSteps: number;
  startTime: number | null;
  error?: string;
}

/**
 * Mensagem de requisi√ß√£o para iniciar replay
 */
export interface ReplayRequest {
  type: 'REPLAY_REQUEST';
  recordingId: string;
  tabId?: number;
  mode?: ReplayMode;
}

/**
 * Mensagem de atualiza√ß√£o de status do replay
 */
export interface ReplayStatusUpdate {
  type: 'REPLAY_STATUS';
  state: ReplayState;
  tabId: number;
}

/**
 * Mensagem para executar a√ß√µes no runner
 */
export interface ReplayExecute {
  type: 'REPLAY_EXECUTE';
  actions: Action[];
  initialUrl: string;
  mode?: ReplayMode;
}

/**
 * Mensagem de resultado da execu√ß√£o
 */
export interface ReplayResult {
  type: 'REPLAY_RESULT';
  success: boolean;
  error?: string;
  completedSteps: number;
}

/**
 * Uni√£o de todos os tipos de mensagem de replay
 */
export type ReplayMessage = 
  | ReplayRequest 
  | ReplayStatusUpdate 
  | ReplayExecute 
  | ReplayResult;

/**
 * Op√ß√µes para o executor de a√ß√µes
 */
export interface ActionExecutorOptions {
  waitTimeout?: number;
  retryAttempts?: number;
  retryDelay?: number;
}

/**
 * Resultado da execu√ß√£o de uma a√ß√£o
 */
export interface ActionExecutionResult {
  success: boolean;
  error?: string;
  duration?: number;
}
